import { connectMongoDB } from "./mongodb";
import { User, Service, Slot, Booking, Review } from "@shared/mongoose-schema";

async function seed() {
  console.log("üå± Seeding MongoDB...");

  try {
    await connectMongoDB();

    // Create sample services with ZAR pricing
    const servicesData = [
