      role: 'admin',
      phone: '+27 11 123 4567',
      address: '123 Admin Street, Johannesburg, 2001',
    });
    console.log("‚úÖ Admin user created (admin@aquashine.co.za / admin123)");

    // Create regular test user
    const userPassword = await bcrypt.hash('user123', 12);
    await User.create({
      email: 'user@example.com',
      password: userPassword,
      firstName: 'John',
      lastName: 'Doe',
      role: 'user',
      phone: '+27 11 987 6543',
      address: '456 Oak Ave, Cape Town, 8001',
    });
    console.log("‚úÖ Regular user created (user@example.com / user123)");

    console.log("üéâ MongoDB seeded successfully!");

  } catch (error) {
    console.error("‚ùå Error seeding MongoDB:", error);
    throw error;
  }
}

export default seed;

// Run seed
seed()
  .then(() => {
    console.log("‚úÖ Seeding completed successfully");
    process.exit(0);
  })
  .catch((error) => {
    console.error("‚ùå Seeding failed:", error);
    process.exit(1);
  });

server/mongodb-storage.ts.content:
import {
  User,
  Service,
  Slot,
  Booking,
  Review,
  type UpsertUser,
  type InsertService,
  type InsertSlot,
  type InsertBooking,
  type InsertReview,
  type IUser,
  type IService,
  type ISlot,
  type IBooking,
  type IReview,
} from "@shared/mongoose-schema";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<IUser | undefined>;
  getUserByEmail(email: string): Promise<IUser | undefined>;
  createUser(user: any): Promise<IUser>;
  upsertUser(user: UpsertUser): Promise<IUser>;

  // Service operations
  getAllServices(): Promise<IService[]>;
  getService(id: string): Promise<IService | undefined>;
  createService(service: InsertService): Promise<IService>;
  updateService(id: string, updates: Partial<InsertService>): Promise<IService>;
  deleteService(id: string): Promise<void>;

  // Slot operations
  getSlotsByService(serviceId: string, date?: string): Promise<ISlot[]>;
  getAvailableSlots(serviceId?: string, date?: string): Promise<ISlot[]>;
  createSlot(slot: InsertSlot): Promise<ISlot>;
  updateSlotBookingStatus(slotId: string, isBooked: boolean): Promise<ISlot>;

  // Booking operations
  getAllBookings(): Promise<IBooking[]>;
  getUserBookings(userId: string): Promise<IBooking[]>;
  getBooking(id: string): Promise<IBooking | undefined>;
  createBooking(booking: InsertBooking): Promise<IBooking>;
  updateBookingStatus(id: string, status: string): Promise<IBooking>;
  updateBookingPaymentStatus(id: string, status: string): Promise<IBooking>;

  // Review operations
  getServiceReviews(serviceId: string): Promise<IReview[]>;
  getAllReviews(): Promise<IReview[]>;
  createReview(review: InsertReview): Promise<IReview>;

  // Admin operations
  getAllUsers(): Promise<IUser[]>;
  deleteUser(id: string): Promise<void>;
  getAnalytics(): Promise<any>;
}

export class MongoDBStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<IUser | undefined> {
    const user = await User.findById(id);
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<IUser | undefined> {
    const user = await User.findOne({ email });
    return user || undefined;
  }

  async createUser(userData: any): Promise<IUser> {
    const user = new User(userData);
    await user.save();
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<IUser> {
    const userId = userData.id;
    delete userData.id; // Remove id from update data

    const user = await User.findByIdAndUpdate(
      userId,
      { $set: userData },
      { new: true, upsert: true, runValidators: true }
    );
    return user!;
  }

  // Service operations
  async getAllServices(): Promise<IService[]> {
    return await Service.find({ isActive: true })
      .sort({ category: 1, price: 1 })
      .exec();
  }

  async getService(id: string): Promise<IService | undefined> {
    const service = await Service.findOne({ 
      _id: id, 
      isActive: true 
    }).exec();
    return service || undefined;
  }

  async createService(service: InsertService): Promise<IService> {
    const newService = new Service(service);
    return await newService.save();
  }

  async updateService(id: string, updates: Partial<InsertService>): Promise<IService> {
    const updatedService = await Service.findByIdAndUpdate(
      id,
      { $set: updates },
      { new: true, runValidators: true }
    );
    if (!updatedService) {
      throw new Error('Service not found');
    }
    return updatedService;
  }

  async deleteService(id: string): Promise<void> {
    await Service.findByIdAndUpdate(id, { isActive: false });
  }

  // Slot operations
  async getSlotsByService(serviceId: string, date?: string): Promise<ISlot[]> {
    const query: any = { serviceId };
    if (date) {
      query.date = date;
    }

    return await Slot.find(query)
      .sort({ date: 1, startTime: 1 })
      .exec();
  }

  async getAvailableSlots(serviceId?: string, date?: string): Promise<ISlot[]> {
    const query: any = { isBooked: false };
    if (serviceId) {
      query.serviceId = serviceId;
    }
    if (date) {
      query.date = date;
    }

    return await Slot.find(query)
      .sort({ date: 1, startTime: 1 })
      .exec();
  }

  async createSlot(slot: InsertSlot): Promise<ISlot> {
    const newSlot = new Slot(slot);
    return await newSlot.save();
  }

  async updateSlotBookingStatus(slotId: string, isBooked: boolean): Promise<ISlot> {
    const updatedSlot = await Slot.findByIdAndUpdate(
      slotId,
      { isBooked },
      { new: true }
    );
    if (!updatedSlot) {
      throw new Error('Slot not found');
    }
    return updatedSlot;
  }

  // Booking operations
  async getAllBookings(): Promise<IBooking[]> {
    return await Booking.find()
      .sort({ createdAt: -1 })
      .exec();
  }

  async getUserBookings(userId: string): Promise<IBooking[]> {
    return await Booking.find({ userId })
      .sort({ createdAt: -1 })
      .exec();
  }

  async getBooking(id: string): Promise<IBooking | undefined> {
    const booking = await Booking.findById(id).exec();
    return booking || undefined;
  }

  async createBooking(booking: InsertBooking): Promise<IBooking> {
    const newBooking = new Booking(booking);
    return await newBooking.save();
  }

  async updateBookingStatus(id: string, status: string): Promise<IBooking> {
    const updatedBooking = await Booking.findByIdAndUpdate(
      id,
      { status },
      { new: true }
    );
    if (!updatedBooking) {
      throw new Error('Booking not found');
    }
    return updatedBooking;
  }

  async updateBookingPaymentStatus(id: string, status: string): Promise<IBooking> {
    const updatedBooking = await Booking.findByIdAndUpdate(
      id,
      { paymentStatus: status },
      { new: true }
    );
    if (!updatedBooking) {
      throw new Error('Booking not found');
    }
    return updatedBooking;
  }

  // Review operations
  async getServiceReviews(serviceId: string): Promise<IReview[]> {
    return await Review.find({ serviceId })
      .sort({ createdAt: -1 })
      .exec();
  }

  async getAllReviews(): Promise<IReview[]> {
    return await Review.find()
      .sort({ createdAt: -1 })
      .exec();
  }

  async createReview(review: InsertReview): Promise<IReview> {
    const newReview = new Review(review);
    return await newReview.save();
  }

  // Admin operations
  async getAllUsers(): Promise<IUser[]> {
    return await User.find()
      .sort({ createdAt: -1 })
      .exec();
  }

  async deleteUser(id: string): Promise<void> {
    await User.findByIdAndDelete(id);
  }

  async getAnalytics(): Promise<any> {
    // Get analytics data for admin dashboard
    const totalBookings = await Booking.countDocuments();
    const totalUsers = await User.countDocuments();

    // Calculate total revenue from paid bookings
    const revenueResult = await Booking.aggregate([
      { $match: { paymentStatus: 'paid' } },
      { $group: { _id: null, total: { $sum: '$totalAmount' } } }
    ]);
    const totalRevenue = revenueResult[0]?.total || 0;

    // Get recent bookings
    const recentBookings = await Booking.find()
      .sort({ createdAt: -1 })
      .limit(10)
      .exec();

    return {
      totalBookings,
      totalRevenue,
      totalUsers,
      recentBookings,
    };
  }
}

export const storage = new MongoDBStorage();

