server/storage.ts.content:
import {
  users,
  services,
  slots,
  bookings,
  reviews,
  type User,
  type UpsertUser,
  type Service,
  type InsertService,
  type Slot,
  type InsertSlot,
  type Booking,
  type InsertBooking,
  type Review,
  type InsertReview,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, isNull, sql } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Service operations
  getAllServices(): Promise<Service[]>;
  getService(id: string): Promise<Service | undefined>;
  createService(service: InsertService): Promise<Service>;
  updateService(id: string, updates: Partial<InsertService>): Promise<Service>;
  deleteService(id: string): Promise<void>;

  // Slot operations
  getSlotsByService(serviceId: string, date?: string): Promise<Slot[]>;
  getAvailableSlots(serviceId?: string, date?: string): Promise<Slot[]>;
  createSlot(slot: InsertSlot): Promise<Slot>;
  updateSlotBookingStatus(slotId: string, isBooked: boolean): Promise<Slot>;

  // Booking operations
  getAllBookings(): Promise<Booking[]>;
  getUserBookings(userId: string): Promise<Booking[]>;
  getBooking(id: string): Promise<Booking | undefined>;
  createBooking(booking: InsertBooking): Promise<Booking>;
  updateBookingStatus(id: string, status: string): Promise<Booking>;
  updateBookingPaymentStatus(id: string, status: string): Promise<Booking>;

  // Review operations
  getServiceReviews(serviceId: string): Promise<Review[]>;
  getAllReviews(): Promise<Review[]>;
  createReview(review: InsertReview): Promise<Review>;

  // Admin operations
  getAllUsers(): Promise<User[]>;
  deleteUser(id: string): Promise<void>;
  getAnalytics(): Promise<any>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Service operations
  async getAllServices(): Promise<Service[]> {
    return await db
      .select()
      .from(services)
      .where(eq(services.isActive, true))
      .orderBy(asc(services.category), asc(services.price));
  }

  async getService(id: string): Promise<Service | undefined> {
    const [service] = await db
      .select()
      .from(services)
      .where(and(eq(services.id, id), eq(services.isActive, true)));
    return service;
  }

  async createService(service: InsertService): Promise<Service> {
    const [newService] = await db
      .insert(services)
      .values(service)
      .returning();
    return newService;
  }

  async updateService(id: string, updates: Partial<InsertService>): Promise<Service> {
    const [updatedService] = await db
      .update(services)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(services.id, id))
      .returning();
    return updatedService;
  }

  async deleteService(id: string): Promise<void> {
    await db
      .update(services)
      .set({ isActive: false, updatedAt: new Date() })
      .where(eq(services.id, id));
  }

  // Slot operations
  async getSlotsByService(serviceId: string, date?: string): Promise<Slot[]> {
    const conditions = [eq(slots.serviceId, serviceId)];
    if (date) {
      conditions.push(eq(slots.date, date));
    }

    return await db
      .select()
      .from(slots)
      .where(and(...conditions))
      .orderBy(asc(slots.date), asc(slots.startTime));
  }

  async getAvailableSlots(serviceId?: string, date?: string): Promise<Slot[]> {
    const conditions = [eq(slots.isBooked, false)];
    if (serviceId) {
      conditions.push(eq(slots.serviceId, serviceId));
    }
    if (date) {
      conditions.push(eq(slots.date, date));
    }

    return await db
      .select()
      .from(slots)
      .where(and(...conditions))
      .orderBy(asc(slots.date), asc(slots.startTime));
  }

  async createSlot(slot: InsertSlot): Promise<Slot> {
    const [newSlot] = await db
      .insert(slots)
      .values(slot)
      .returning();
    return newSlot;
  }

  async updateSlotBookingStatus(slotId: string, isBooked: boolean): Promise<Slot> {
    const [updatedSlot] = await db
      .update(slots)
      .set({ isBooked })
      .where(eq(slots.id, slotId))
      .returning();
    return updatedSlot;
  }

  // Booking operations
  async getAllBookings(): Promise<Booking[]> {
    return await db
      .select()
      .from(bookings)
      .orderBy(desc(bookings.createdAt));
  }

  async getUserBookings(userId: string): Promise<Booking[]> {
    return await db
      .select()
      .from(bookings)
      .where(eq(bookings.userId, userId))
      .orderBy(desc(bookings.createdAt));
  }

  async getBooking(id: string): Promise<Booking | undefined> {
    const [booking] = await db
      .select()
      .from(bookings)
      .where(eq(bookings.id, id));
    return booking;
  }

  async createBooking(booking: InsertBooking): Promise<Booking> {
    const [newBooking] = await db
      .insert(bookings)
      .values(booking)
      .returning();
    return newBooking;
  }

  async updateBookingStatus(id: string, status: string): Promise<Booking> {
    const [updatedBooking] = await db
      .update(bookings)
      .set({ status, updatedAt: new Date() })
      .where(eq(bookings.id, id))
      .returning();
    return updatedBooking;
  }

  async updateBookingPaymentStatus(id: string, status: string): Promise<Booking> {
    const [updatedBooking] = await db
      .update(bookings)
      .set({ paymentStatus: status, updatedAt: new Date() })
      .where(eq(bookings.id, id))
      .returning();
    return updatedBooking;
  }

  // Review operations
  async getServiceReviews(serviceId: string): Promise<Review[]> {
    return await db
      .select()
      .from(reviews)
      .where(eq(reviews.serviceId, serviceId))
      .orderBy(desc(reviews.createdAt));
  }

  async getAllReviews(): Promise<Review[]> {
    return await db
      .select()
      .from(reviews)
      .orderBy(desc(reviews.createdAt));
  }

  async createReview(review: InsertReview): Promise<Review> {
    const [newReview] = await db
      .insert(reviews)
      .values(review)
      .returning();
    return newReview;
  }

  // Admin operations
  async getAllUsers(): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .orderBy(desc(users.createdAt));
  }

  async deleteUser(id: string): Promise<void> {
    // In a real app, you might want to soft delete or handle related data
    await db.delete(users).where(eq(users.id, id));
  }

  async getAnalytics(): Promise<any> {
    // Get analytics data for admin dashboard
    const totalBookings = await db
      .select({ count: sql`count(*)` })
      .from(bookings);

    const totalRevenue = await db
      .select({ 
        revenue: sql`sum(${bookings.totalAmount})`.mapWith((value) => parseFloat(value || '0'))
      })
      .from(bookings)
      .where(eq(bookings.paymentStatus, 'paid'));

    const totalUsers = await db
      .select({ count: sql`count(*)` })
      .from(users);

    const recentBookings = await db
      .select()
      .from(bookings)
      .orderBy(desc(bookings.createdAt))
      .limit(10);

    return {
      totalBookings: parseInt(totalBookings[0]?.count?.toString() || '0'),
      totalRevenue: totalRevenue[0]?.revenue || 0,
      totalUsers: parseInt(totalUsers[0]?.count?.toString() || '0'),
      recentBookings,
    };
  }
}

export const storage = new DatabaseStorage();

