import mongoose, { Schema, Document } from 'mongoose';
import { z } from 'zod';

// User interface and schema
export interface IUser extends Document {
  _id: string;
  email: string;
  password: string;
  firstName?: string;
  lastName?: string;
  profileImageUrl?: string;
  role: string;
  phone?: string;
  address?: string;
  createdAt: Date;
  updatedAt: Date;
}

const userSchema = new Schema<IUser>({
  email: { type: String, required: true, unique: true },
  password: { type: String, required: true },
  firstName: String,
  lastName: String,
  profileImageUrl: String,
  role: { type: String, default: 'user' },
  phone: String,
  address: String,
}, {
  timestamps: true,
});

export const User = mongoose.model<IUser>('User', userSchema);

// Service interface and schema
export interface IService extends Document {
  _id: string;
  name: string;
  description: string;
  price: number;
  duration: number;
  category: string;
  features: string[];
  isActive: boolean;
  createdAt: Date;
  updatedAt: Date;
}

const serviceSchema = new Schema<IService>({
  name: { type: String, required: true },
  description: { type: String, required: true },
  price: { type: Number, required: true },
  duration: { type: Number, required: true },
  category: { type: String, default: 'basic' },
  features: [String],
  isActive: { type: Boolean, default: true },
}, {
  timestamps: true,
});

export const Service = mongoose.model<IService>('Service', serviceSchema);

// Slot interface and schema
export interface ISlot extends Document {
  _id: string;
  serviceId: string;
  date: string;
  startTime: string;
  endTime: string;
  isBooked: boolean;
  createdAt: Date;
}

const slotSchema = new Schema<ISlot>({
  serviceId: { type: String, required: true, ref: 'Service' },
  date: { type: String, required: true },
  startTime: { type: String, required: true },
  endTime: { type: String, required: true },
  isBooked: { type: Boolean, default: false },
}, {
  timestamps: { createdAt: true, updatedAt: false },
});

export const Slot = mongoose.model<ISlot>('Slot', slotSchema);

// Booking interface and schema
export interface IBooking extends Document {
  _id: string;
  userId: string;
  serviceId: string;
  slotId: string;
  vehicleType: string;
  vehicleBrand: string;
  vehicleModel: string;
  manufacturingYear: number;
  registrationPlate: string;
  status: string;
  totalAmount: number;
  paymentStatus: string;
  paymentMethod?: string;
  stripePaymentIntentId?: string;
  createdAt: Date;
  updatedAt: Date;
}

const bookingSchema = new Schema<IBooking>({
  userId: { type: String, required: true, ref: 'User' },
  serviceId: { type: String, required: true, ref: 'Service' },
  slotId: { type: String, required: true, ref: 'Slot' },
  vehicleType: { type: String, required: true },
  vehicleBrand: { type: String, required: true },
  vehicleModel: { type: String, required: true },
  manufacturingYear: { type: Number, required: true },
  registrationPlate: { type: String, required: true },
  status: { type: String, default: 'pending' },
  totalAmount: { type: Number, required: true },
  paymentStatus: { type: String, default: 'pending' },
  paymentMethod: String,
  stripePaymentIntentId: String,
}, {
  timestamps: true,
});

export const Booking = mongoose.model<IBooking>('Booking', bookingSchema);

// Review interface and schema
export interface IReview extends Document {
  _id: string;
  userId: string;
  serviceId: string;
  bookingId: string;
  rating: number;
  comment?: string;
  createdAt: Date;
}

const reviewSchema = new Schema<IReview>({
  userId: { type: String, required: true, ref: 'User' },
  serviceId: { type: String, required: true, ref: 'Service' },
  bookingId: { type: String, required: true, ref: 'Booking' },
  rating: { type: Number, required: true, min: 1, max: 5 },
  comment: String,
}, {
  timestamps: { createdAt: true, updatedAt: false },
});

export const Review = mongoose.model<IReview>('Review', reviewSchema);

// Zod validation schemas
export const insertUserSchema = z.object({
  email: z.string().email().optional(),
  firstName: z.string().optional(),
  lastName: z.string().optional(),
  profileImageUrl: z.string().optional(),
  role: z.string().default('user'),
  phone: z.string().optional(),
  address: z.string().optional(),
});

export const insertServiceSchema = z.object({
  name: z.string().min(1),
  description: z.string().min(1),
  price: z.number().positive(),
  duration: z.number().positive(),
  category: z.string().default('basic'),
  features: z.array(z.string()).default([]),
  isActive: z.boolean().default(true),
});

export const insertSlotSchema = z.object({
  serviceId: z.string().min(1),
  date: z.string().regex(/^\d{4}-\d{2}-\d{2}$/),
  startTime: z.string().regex(/^\d{2}:\d{2}$/),
  endTime: z.string().regex(/^\d{2}:\d{2}$/),
  isBooked: z.boolean().default(false),
});

export const insertBookingSchema = z.object({
  userId: z.string().min(1),
  serviceId: z.string().min(1),
  slotId: z.string().min(1),
  vehicleType: z.enum(['car', 'suv', 'truck', 'van', 'motorcycle']),
  vehicleBrand: z.enum(['Toyota', 'Honda', 'Ford', 'BMW', 'Mercedes', 'Audi', 'Nissan', 'Hyundai', 'Tata', 'Runner', 'Other']),
  vehicleModel: z.string().min(1),
  manufacturingYear: z.number().min(1900).max(new Date().getFullYear() + 1),
  registrationPlate: z.string().min(1),
  totalAmount: z.number().positive(),
  paymentMethod: z.enum(['cash', 'card']).optional(),
});

export const insertReviewSchema = z.object({
  userId: z.string().min(1),
  serviceId: z.string().min(1),
  bookingId: z.string().min(1),
  rating: z.number().min(1).max(5),
  comment: z.string().optional(),
});

// Auth schemas
export const registerSchema = z.object({
  email: z.string().email(),
  password: z.string().min(6),
  firstName: z.string().optional(),
  lastName: z.string().optional(),
  phone: z.string().optional(),
  address: z.string().optional(),
});

export const loginSchema = z.object({
  email: z.string().email(),
  password: z.string().min(1),
});

// Types
export type UpsertUser = z.infer<typeof insertUserSchema> & { id?: string };
export type InsertService = z.infer<typeof insertServiceSchema>;
export type InsertSlot = z.infer<typeof insertSlotSchema>;
export type InsertBooking = z.infer<typeof insertBookingSchema>;
export type InsertReview = z.infer<typeof insertReviewSchema>;

// Export types for compatibility
export type {
  IUser as User,
  IService as Service,
  ISlot as Slot,
  IBooking as Booking,
  IReview as Review,
};

