}

interface Slot {
  id: string;
  serviceId: string;
  date: string;
  startTime: string;
  endTime: string;
  isBooked: boolean;
}

interface BookingModalProps {
  isOpen: boolean;
  onClose: () => void;
  selectedService?: Service;
}

export default function BookingModal({ isOpen, onClose, selectedService }: BookingModalProps) {
  const [step, setStep] = useState(1);
  const [selectedSlot, setSelectedSlot] = useState<Slot | null>(null);
  const [bookingData, setBookingData] = useState({
    vehicleType: "",
    vehicleBrand: "",
    vehicleModel: "",
    manufacturingYear: "",
    registrationPlate: "",
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: availableSlots, isLoading: slotsLoading } = useQuery({
    queryKey: ["/api/slots/availability", selectedService?.id],
    enabled: !!selectedService?.id,
  });

  const bookingMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/bookings", data);
    },
    onSuccess: () => {
      toast({
        title: "Booking Confirmed!",
        description: "Your appointment has been scheduled successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/bookings"] });
      onClose();
      resetForm();
    },
    onError: (error: Error) => {
      toast({
        title: "Booking Failed",
        description: error.message || "Failed to create booking. Please try again.",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setStep(1);
    setSelectedSlot(null);
    setBookingData({
      vehicleType: "",
      vehicleBrand: "",
      vehicleModel: "",
      manufacturingYear: "",
      registrationPlate: "",
    });
  };

  const handleSlotSelect = (slot: Slot) => {
    setSelectedSlot(slot);
  };

  const handleInputChange = (field: string, value: string) => {
    setBookingData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSubmit = () => {
    if (!selectedService || !selectedSlot) return;

    const bookingPayload = {
      serviceId: selectedService.id,
      slotId: selectedSlot.id,
      vehicleType: bookingData.vehicleType,
      vehicleBrand: bookingData.vehicleBrand,
      vehicleModel: bookingData.vehicleModel,
      manufacturingYear: parseInt(bookingData.manufacturingYear),
      registrationPlate: bookingData.registrationPlate,
      totalAmount: selectedService.price,
    };

    bookingMutation.mutate(bookingPayload);
  };

  const canProceedToStep2 = selectedSlot !== null;
  const canSubmit = Object.values(bookingData).every(value => value !== "");

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="glass-effect border-border max-w-2xl">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            Book Your Service
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              data-testid="button-close-modal"
            >
              <X className="w-4 h-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        {/* Progress Steps */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center space-x-2">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center text-white font-semibold ${
              step >= 1 ? 'bg-gradient-to-r from-primary to-accent' : 'bg-muted'
            }`}>
              1
            </div>
            <span className="text-sm font-medium">Select Slot</span>
          </div>
          <div className="flex-1 h-1 bg-muted mx-4 rounded-full">
            <div className={`h-1 bg-gradient-to-r from-primary to-accent rounded-full transition-all duration-500 ${
              step >= 2 ? 'w-full' : 'w-0'
            }`}></div>
          </div>
          <div className="flex items-center space-x-2">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center text-white font-semibold ${
              step >= 2 ? 'bg-gradient-to-r from-primary to-accent' : 'bg-muted'
            }`}>
              2
            </div>
            <span className="text-sm font-medium">Vehicle Details</span>
          </div>
        </div>

        {/* Step 1: Slot Selection */}
        {step === 1 && (
          <div className="space-y-6">
            {selectedService && (
              <div className="glass-effect p-4 rounded-xl">
                <div className="flex items-center justify-between">
                  <div>
                    <h3 className="font-semibold text-lg" data-testid="selected-service-name">
                      {selectedService.name}
                    </h3>
                    <p className="text-muted-foreground text-sm">{selectedService.description}</p>
                  </div>
                  <div className="text-right">
                    <div className="text-2xl font-bold text-gradient" data-testid="selected-service-price">
                      ${selectedService.price}
                    </div>
                    <div className="text-sm text-muted-foreground flex items-center">
                      <Clock className="w-4 h-4 mr-1" />
                      {selectedService.duration} min
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div>
              <Label className="text-base font-semibold mb-4 block">Select Available Time Slot</Label>
              {slotsLoading ? (
                <div className="space-y-3">
                  {Array.from({ length: 4 }).map((_, i) => (
                    <div key={i} className="glass-effect p-4 rounded-xl animate-pulse">
                      <div className="flex justify-between">
                        <div className="space-y-2">
                          <div className="h-4 bg-muted rounded w-32"></div>
                          <div className="h-3 bg-muted rounded w-24"></div>
                        </div>
                        <div className="h-8 bg-muted rounded w-20"></div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : availableSlots?.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No available slots for this service. Please try again later.
                </div>
              ) : (
                <div className="space-y-3 max-h-64 overflow-y-auto">
                  {availableSlots?.map((slot: Slot) => (
                    <div
                      key={slot.id}
                      onClick={() => handleSlotSelect(slot)}
                      className={`glass-effect p-4 rounded-xl cursor-pointer transition-all duration-300 ${
                        selectedSlot?.id === slot.id
                          ? 'ring-2 ring-primary bg-primary/10'
                          : 'hover:bg-white/10'
                      }`}
                      data-testid={`slot-${slot.id}`}
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                          <div className="w-3 h-3 bg-green-400 rounded-full animate-bounce-subtle"></div>
                          <div>
                            <div className="font-semibold flex items-center space-x-2">
                              <Calendar className="w-4 h-4" />
                              <span>{slot.date}</span>
                            </div>
                            <div className="text-sm text-muted-foreground flex items-center space-x-2">
                              <Clock className="w-4 h-4" />
                              <span>{slot.startTime} - {slot.endTime}</span>
                            </div>
                          </div>
                        </div>
                        <Badge className="bg-green-500/20 text-green-400">Available</Badge>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="flex justify-end space-x-4">
              <Button
                variant="outline"
                onClick={onClose}
                className="glass-effect border-border hover:bg-white/20"
                data-testid="button-cancel-step1"
              >
                Cancel
              </Button>
              <Button
                onClick={() => setStep(2)}
                disabled={!canProceedToStep2}
                className="ripple-effect bg-gradient-to-r from-primary to-accent text-primary-foreground"
                data-testid="button-continue-step2"
              >
                Continue
              </Button>
            </div>
          </div>
        )}

        {/* Step 2: Vehicle Details */}
        {step === 2 && (
          <div className="space-y-6">
            <div className="glass-effect p-4 rounded-xl">
              <h3 className="font-semibold text-lg mb-2">Selected Slot</h3>
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <Calendar className="w-5 h-5 text-primary" />
                  <span>{selectedSlot?.date}</span>
                  <Clock className="w-5 h-5 text-primary" />
                  <span>{selectedSlot?.startTime} - {selectedSlot?.endTime}</span>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setStep(1)}
                  data-testid="button-change-slot"
                >
                  Change
                </Button>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="vehicleType">Vehicle Type</Label>
                <Select
                  value={bookingData.vehicleType}
                  onValueChange={(value) => handleInputChange('vehicleType', value)}
                >
                  <SelectTrigger className="glass-effect border-border" data-testid="select-vehicle-type">
                    <SelectValue placeholder="Select vehicle type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="car">Car</SelectItem>
                    <SelectItem value="suv">SUV</SelectItem>
                    <SelectItem value="truck">Truck</SelectItem>
                    <SelectItem value="van">Van</SelectItem>
                    <SelectItem value="motorcycle">Motorcycle</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="vehicleBrand">Vehicle Brand</Label>
                <Select
                  value={bookingData.vehicleBrand}
                  onValueChange={(value) => handleInputChange('vehicleBrand', value)}
                >
                  <SelectTrigger className="glass-effect border-border" data-testid="select-vehicle-brand">
                    <SelectValue placeholder="Select brand" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Toyota">Toyota</SelectItem>
                    <SelectItem value="Honda">Honda</SelectItem>
                    <SelectItem value="Ford">Ford</SelectItem>
                    <SelectItem value="BMW">BMW</SelectItem>
                    <SelectItem value="Mercedes">Mercedes</SelectItem>
                    <SelectItem value="Audi">Audi</SelectItem>
                    <SelectItem value="Nissan">Nissan</SelectItem>
                    <SelectItem value="Hyundai">Hyundai</SelectItem>
                    <SelectItem value="Tata">Tata</SelectItem>
                    <SelectItem value="Runner">Runner</SelectItem>
                    <SelectItem value="Other">Other</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="vehicleModel">Vehicle Model</Label>
                <Input
                  id="vehicleModel"
                  placeholder="e.g., Camry"
                  value={bookingData.vehicleModel}
                  onChange={(e) => handleInputChange('vehicleModel', e.target.value)}
                  className="glass-effect border-border"
                  data-testid="input-vehicle-model"
                />
              </div>

              <div>
                <Label htmlFor="manufacturingYear">Manufacturing Year</Label>
                <Input
                  id="manufacturingYear"
                  type="number"
                  placeholder="2020"
                  value={bookingData.manufacturingYear}
                  onChange={(e) => handleInputChange('manufacturingYear', e.target.value)}
                  className="glass-effect border-border"
                  data-testid="input-manufacturing-year"
                />
              </div>

              <div className="md:col-span-2">
                <Label htmlFor="registrationPlate">Registration Plate</Label>
                <Input
                  id="registrationPlate"
                  placeholder="ABC123"
                  value={bookingData.registrationPlate}
                  onChange={(e) => handleInputChange('registrationPlate', e.target.value)}
                  className="glass-effect border-border"
                  data-testid="input-registration-plate"
                />
              </div>
            </div>

            <div className="flex justify-between space-x-4">
              <Button
                variant="outline"
                onClick={() => setStep(1)}
                className="glass-effect border-border hover:bg-white/20"
                data-testid="button-back-step1"
              >
                Back
              </Button>
              <div className="flex space-x-4">
                <Button
                  variant="outline"
                  onClick={onClose}
                  className="glass-effect border-border hover:bg-white/20"
                  data-testid="button-cancel-step2"
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleSubmit}
                  disabled={!canSubmit || bookingMutation.isPending}
                  className="ripple-effect bg-gradient-to-r from-primary to-accent text-primary-foreground"
                  data-testid="button-confirm-booking"
                >
                  {bookingMutation.isPending ? 'Booking...' : 'Confirm Booking'}
                </Button>
              </div>
            </div>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ReviewCard.tsx.content:
import { Star, User } from "lucide-react";
import { FloatingCard } from "@/components/ui/floating-card";

interface Review {
  id: string;
  rating: number;
  comment: string;
  user: {
    firstName?: string;
    lastName?: string;
    email?: string;
    profileImageUrl?: string;
  };
  service: {
    name: string;
  };
  createdAt: string;
}

interface ReviewCardProps {
  review: Review;
}

export default function ReviewCard({ review }: ReviewCardProps) {
  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`w-4 h-4 ${
          i < rating 
            ? "text-yellow-400 fill-current" 
            : "text-muted-foreground"
        }`}
      />
    ));
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getUserDisplayName = () => {
    if (review.user.firstName && review.user.lastName) {
      return `${review.user.firstName} ${review.user.lastName}`;
    }
    if (review.user.firstName) {
      return review.user.firstName;
    }
    return review.user.email?.split('@')[0] || 'Anonymous User';
  };

  return (
    <FloatingCard className="p-8" data-testid={`review-card-${review.id}`}>
      <div className="flex items-center mb-6">
        <div className="flex text-yellow-400 mr-4" data-testid={`review-rating-${review.id}`}>
          {renderStars(review.rating)}
        </div>
        <span className="text-sm text-muted-foreground">{review.rating}.0</span>
      </div>

      <p className="text-lg mb-6 leading-relaxed" data-testid={`review-comment-${review.id}`}>
        {review.comment}
      </p>

      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          {review.user.profileImageUrl ? (
            <img 
              src={review.user.profileImageUrl} 
              alt="Customer photo" 
              className="w-12 h-12 rounded-full object-cover"
              data-testid={`review-avatar-${review.id}`}
            />
          ) : (
            <div className="w-12 h-12 rounded-full bg-gradient-to-r from-primary to-accent flex items-center justify-center">
              <User className="text-white w-6 h-6" />
            </div>
          )}
          <div>
            <div className="font-semibold" data-testid={`review-user-name-${review.id}`}>
              {getUserDisplayName()}
            </div>
            <div className="text-sm text-muted-foreground" data-testid={`review-service-${review.id}`}>
              {review.service.name}
            </div>
          </div>
        </div>
        <div className="text-right">
          <div className="text-sm text-muted-foreground" data-testid={`review-date-${review.id}`}>
            {formatDate(review.createdAt)}
          </div>
        </div>
      </div>
    </FloatingCard>
  );
}

client/src/components/ServiceCard.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import BookingModal from "./BookingModal";
import { Car, Clock, Star, Sparkles } from "lucide-react";

interface Service {
  id: string;
  name: string;
  description: string;
  price: string;
  duration: number;
  category: string;
  features?: string[];
}

interface ServiceCardProps {
  service: Service;
}

export default function ServiceCard({ service }: ServiceCardProps) {
  const [isBookingModalOpen, setIsBookingModalOpen] = useState(false);

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'premium':
        return <Sparkles className="text-white" />;
      case 'detailing':
        return <Star className="text-white" />;
      default:
        return <Car className="text-white" />;
    }
  };

  const getCategoryGradient = (category: string) => {
    switch (category) {
      case 'premium':
        return 'from-primary to-accent';
      case 'detailing':
        return 'from-accent to-secondary';
      default:
        return 'from-green-500 to-primary';
    }
  };

  return (
    <>
      <div className="service-card group">
        <div className="service-card-inner glass-effect p-8 rounded-3xl floating-card relative overflow-hidden">
          <div className={`absolute inset-0 bg-gradient-to-br ${getCategoryGradient(service.category)}/20 opacity-0 group-hover:opacity-100 transition-opacity duration-500`}></div>

          <div className="relative z-10">
            <div className="flex items-center justify-between mb-6">
              <div className={`w-16 h-16 bg-gradient-to-r ${getCategoryGradient(service.category)} rounded-2xl flex items-center justify-center group-hover:scale-110 transition-transform duration-300`}>
                {getCategoryIcon(service.category)}
              </div>
              <Badge 
                variant="secondary" 
                className="bg-muted/50 text-muted-foreground capitalize"
                data-testid={`service-category-${service.id}`}
              >
                {service.category}
              </Badge>
            </div>

            <h3 className="text-2xl font-bold mb-4" data-testid={`service-name-${service.id}`}>
              {service.name}
            </h3>
            <p className="text-muted-foreground mb-6" data-testid={`service-description-${service.id}`}>
              {service.description}
            </p>

            <div className="flex items-center justify-between mb-6">
              <div className="flex items-center space-x-4">
                <span className="text-3xl font-bold text-gradient" data-testid={`service-price-${service.id}`}>
                  ${service.price}
                </span>
                <div className="text-sm text-muted-foreground">
                  <div className="flex items-center space-x-1">
                    <Clock className="w-4 h-4" />
                    <span data-testid={`service-duration-${service.id}`}>{service.duration} min</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Features */}
            {service.features && service.features.length > 0 && (
              <div className="mb-6">
                <div className="flex flex-wrap gap-2">
                  {service.features.slice(0, 3).map((feature, index) => (
                    <Badge 
                      key={index}
                      variant="outline" 
                      className="text-xs border-primary/30 text-primary"
                      data-testid={`service-feature-${service.id}-${index}`}
                    >
                      {feature}
                    </Badge>
                  ))}
                  {service.features.length > 3 && (
                    <Badge variant="outline" className="text-xs border-muted text-muted-foreground">
                      +{service.features.length - 3} more
                    </Badge>
                  )}
                </div>
              </div>
            )}

            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-1">
                <div className="flex text-yellow-400">
                  <Star className="w-4 h-4 fill-current" />
                  <Star className="w-4 h-4 fill-current" />
                  <Star className="w-4 h-4 fill-current" />
                  <Star className="w-4 h-4 fill-current" />
                  <Star className="w-4 h-4 fill-current" />
                </div>
                <span className="text-sm text-muted-foreground">4.9 (124)</span>
              </div>
              <Button
                onClick={() => setIsBookingModalOpen(true)}
                className={`ripple-effect bg-gradient-to-r ${getCategoryGradient(service.category)} text-primary-foreground px-6 py-2 rounded-lg font-semibold hover:shadow-lg transition-all duration-300`}
                data-testid={`button-quick-book-${service.id}`}
              >
                Quick Book
              </Button>
            </div>
          </div>
        </div>
      </div>

      <BookingModal
        isOpen={isBookingModalOpen}
        onClose={() => setIsBookingModalOpen(false)}
        selectedService={service}
      />
    </>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

